cat << EOF > /tmp/default-debug.ini
#### Customize debug settings here
#### Note: This file is automatically generated by setup.sh and is ignored by git, so
#### changes will not be tracked. For defaults, see setup.sh.

error_reporting=E_ALL
display_errors=1
EOF

create_debug_ini() {
  if [[ ! -e "config/$1/debug-web.ini" ]]; then
    echo "Creating config/$1/debug-web.ini."
    cp /tmp/default-debug.ini "config/$1/debug-web.ini"
    echo
  fi

  if [[ ! -e "config/$1/debug-cli.ini" ]]; then
    echo "Creating config/$1/debug-cli.ini."
    cp /tmp/default-debug.ini "config/$1/debug-cli.ini"
    echo
  fi
}

create_debug_ini_all() {
  declare -a debug_config_files=(
    "civicrm"
    "civiproxy"
    "core"
    "payments"
    "privatebin"
    "email-pref-ctr"
    "donut"
    "smashpig"
  )

  for i in "${debug_config_files[@]}"
  do
    create_debug_ini $i
  done
}

